CREATE TABLE "public"."bells"("id" text NOT NULL, "name" text NOT NULL, "description" text, "state" text NOT NULL, "context" jsonb NOT NULL DEFAULT '{}'::jsonb, "created_at" timestamptz NOT NULL DEFAULT now(), "last_updated" timestamptz NOT NULL DEFAULT now(), "started_at" timestamptz NOT NULL DEFAULT now(), "root_block_id" text, PRIMARY KEY ("id") , FOREIGN KEY ("state") REFERENCES "public"."blockState"("value") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("root_block_id") REFERENCES "public"."blocks"("id") ON UPDATE restrict ON DELETE restrict, UNIQUE ("id"));
